													  WHERE ID = A056_FORM30.ID FOR XML PATH ('')
													 ), 1, 1, '')
		 ELSE OBSERVACION END) AS OBSERVACION,
		 COALESCE(
					(
						SELECT
							TOP 1
							E.ACRONIMO
						FROM ENTERPRISE E
						WHERE E.IDEMPRESA = @IDEMPRESA
					), ''
				) AS ACRO_EMPRESA,
		 [dbo].[V4MVC_FSTR_RUT](@IDEMPRESA) AS STRUT_EMPRESA,
		 COALESCE(
					(
						SELECT
