       ,@JUE NVARCHAR(4),@VIE NVARCHAR(4),@SAB NVARCHAR(4),@DOM NVARCHAR(4),@LIMITE NVARCHAR(8),@strSQL as nvarchar(MAX),@DIAS NVARCHAR(100)

