	  ,@HORAFIN_COL=HORAFIN_COL 
	  ,@LUN=LUN 
	  ,@MAR=MAR 
	  ,@MIE=MIE 
	  ,@JUE=JUE 
	  ,@VIE=VIE 
	  ,@SAB=SAB 
      ,@DOM=DOM 
FROM TAB_REVISIONVEH WHERE ACTIVO='SI'
SET @LIMITE = CONVERT(varchar,DATEADD(hour,1,GETDATE()),8)
SET @DIAS = '0'


		SET @HorasD1 = (DATEDIFF(HOUR,@HORAINI,@HORAINI_COL) + DATEDIFF(HOUR,@HORAFIN_COL,@HORAFIN) )*@EXPD 
