		'AND ISNULL(ERROR,'+ N'''' + N'''' +')'+ '=' + N'''' + N'''' + ')'+ ' ' +'AS CC' + ' ' +       
		') src' +' '+        
		'pivot' +        
		'(' +        
		'COUNT(RUT) for [SERVICIO] in (' + @STRING_SQL_SUBSTRING + ')' +        
		') AS PIV' + ' '+  
		'SELECT DIVISION,('+ @STRING_SQL_CONVERT_SUBSTRING +') AS DATA_GRAFIC FROM #TEMP_DATA ORDER BY DIVISION ASC' + ' '   
		)    


END  


