 ,@IDEMPRESA	NVARCHAR(10)
 ,@PERIODO		NVARCHAR(6)
) AS
BEGIN
	SELECT
		 PERIODO,
		 EMPRESA,
		 MONTO,
		 ID,
		 VALIDADO,
		 OBSERVACION,
		 COALESCE(
					(
						SELECT
							TOP 1
							E.ACRONIMO
						FROM ENTERPRISE E
						WHERE E.IDEMPRESA = @IDEMPRESA
					), ''
				) AS ACRO_EMPRESA,
		 [dbo].[V4MVC_FSTR_RUT](@IDEMPRESA) AS STRUT_EMPRESA,
		 COALESCE(
			(
				SELECT
					TOP 1
						DD.ID_DISPUTA
				FROM DOCS_DISPUTA_CLABORAL DD
				WHERE
					DD.EMPRESA = CERT_DEUDAS.EMPRESA AND
					DD.PERIODO = CERT_DEUDAS.PERIODO AND
					DD.ID_DOC = @ID_DOC AND
					DD.ID = CERT_DEUDAS.ID
				ORDER BY DD.ID_DISPUTA DESC
			), -1
		 ) AS DISPUTA_ID,
		 IIF(EXISTS(
				SELECT * 
				FROM DOCS_DISPUTA_CLABORAL DD
				WHERE
					DD.EMPRESA = CERT_DEUDAS.EMPRESA AND
					DD.PERIODO = CERT_DEUDAS.PERIODO AND
					DD.ID_DOC = @ID_DOC AND
					DD.ID = CERT_DEUDAS.ID
				ORDER BY DD.ID_DISPUTA DESC
				OFFSET 0 ROWS
		 ), 1 , 0) AS DISPUTA_EXISTE,
		 COALESCE(
			(
				SELECT
					TOP 1
					ARCHIVO
				FROM ARCHIVOS A
				WHERE A.ID = CERT_DEUDAS.ID
			), NULL
		 ) AS ADJUNTO_CONTENIDO,
		 COALESCE(
			(
				SELECT
					TOP 1
					TIPOCONTENIDO
				FROM ARCHIVOS A
				WHERE A.ID = CERT_DEUDAS.ID
			), NULL
		 ) AS ADJUNTO_TIPOCONTENIDO,
		 COALESCE(
			(
				SELECT
					TOP 1
					NOMBRE
				FROM ARCHIVOS A
				WHERE A.ID = CERT_DEUDAS.ID
			), NULL
		 ) AS ADJUNTO_NOMBRE	
	FROM CERT_DEUDAS
	WHERE
		EMPRESA = @IDEMPRESA AND
		PERIODO = @PERIODO
END
