	DECLARE @CS_HORAMOD NVARCHAR(8)
	DECLARE @CS_CERTUSUARIO NVARCHAR(20)
	DECLARE @CS_CERTFECHA NVARCHAR(8)
	DECLARE @CS_CERTHORA NVARCHAR(8)

	
	DECLARE @DSP_ID_DISPUTA INT
