	DECLARE @DSP_VALIDADO NVARCHAR(2)
	DECLARE @DSP_PERIODO NVARCHAR(6)
	DECLARE @DSP_ID_CERTIFICADO INT

	SET @DISPUTA_EXISTE = 0
	SET @DISPUTA_EMPRESA = ''
	SET @DISPUTA_FECHA = ''

	SELECT
		TOP 1
		@CS_EXISTS = 1,
		@CS_EMPRESA = EMPRESA,
		@CS_PERIODO = PERIODO,

		@CS_VALIDADO = VALIDADO,
