(
  @DISPUTA_ID	INT
) AS
BEGIN
	SELECT
		TOP 1
		DD.USUARIO,
		DD.FECHA,
		DD.HORA,
		DD.CERTUSUARIO,
		DD.CERTFECHA,
		DD.CERTHORA,
		DD.REVALUSUARIO,
		DD.REVALFECHA,
		DD.REVALHORA,
		DD.EMPRESA,
		DD.DIVISION,
		DD.OST,
		DD.PERIODO,
		DD.ID,
		DD.OBSERVACION,
		DD.VALIDADO,
		DD.ESTADO,
		DD.OBSERVACION_REVAL,
		DD.ID_DOC,
		DD.ID_CERTIFICADO,
		E.ACRONIMO,
		D.NOMBRE,
		DD.ID_DISPUTA,
		[dbo].[V4MVC_FSTR_RUT](E.IDEMPRESA) AS STRUT_EMPRESA,
		COALESCE(
					(
						SELECT
							TOP 1
							CONCAT(NOMBRES, ' ', APELLIDOS)
