		            	Set @strSQL = @strSQL + N' AND (C.PATENTE = ''' + @PATENTE + ''')'
		            END

		            IF @CAMPO = 'INGRESO'
		            BEGIN
		            	Set @strSQL = @strSQL + N' AND (INGRESO > 0 )'
		            END
					ELSE IF @CAMPO = 'SALIDA'
					BEGIN
					     Set @strSQL = @strSQL + N' AND (SALIDA > 0 )'
