	AND CR.RUT						=		ISNULL(@RUT, CR.RUT)
	AND ASISTIO						=		'SI' 
	GROUP BY D.DIVISION,D.DIVCOD, CP.CODIGO, CH.CHARLA, CP.FECHA, CP.HORA, CP.FINALIZADO
	ORDER BY CP.FECHA, CP.HORA ASC 

END


