	--BEGIN

		SELECT DISTINCT C.SERVICIO		,C.LOCAL		,C.RUT		,C.EMPRESA
						,C.ACRONIMO		,C.FECHA		,C.HORA		,C.CANTIDAD
						,(CASE WHEN ISNULL(C.ERROR, '') <> '' THEN ISNULL(M.MSG_REPORT, 'ERROR') ELSE '' END  ) AS ERROR
						,C.CCOSTO		,C.COD_PDA		,W.NOMBRES	,W.APELLIDOS
