		FROM WC3_VISTA_CASINO			AS C 
		LEFT OUTER JOIN WORKERS			AS W	ON C.RUT = W.RUT 
		LEFT OUTER JOIN MULTASTTO		AS M	ON C.ERROR = M.CODMULTA 
		INNER JOIN A024_DIVISIONES D			ON C.DIVISION = D.DIVCOD 
