		AND (EMPRESA = @EMPRESA)  
		AND (NIVEL = @NIVEL) 
		AND L.ID_DOC = @ID_DOC
		AND VALIDADO IN ('SI','RE') 
		ORDER BY FECHAFASE_1,HORAFASE_1,FASE DESC

END

