			  ) OR
			  (O.DESCRIPOST IS NULL OR O.DESCRIPOST = '')
			)
	) Y

	IF(@SORT_DIRECTION = 'ASC')
		BEGIN
			SELECT
				X.NROOST,
				X.DESCRIPOST,
				[dbo].[V4MVC_FGET_FEC_USER](X.FECINICIO) AS FECINICIO,
				[dbo].[V4MVC_FGET_FEC_USER](X.FECTERM) AS FECTERM
