				INNER JOIN OSTARBOL OA
					ON OA.OST = O.NROOST
				WHERE
					(OA.EMPRESA = @IDEMPRESA OR OA.MADRE = @IDEMPRESA) AND
					(O.FECTERM >= CONVERT(VARCHAR(8),GETDATE(),112)) AND
					(
