(
  @ID_DOC			INT
 ,@EXT				NVARCHAR(10)
 ,@NROOST			NVARCHAR(50)
 ,@MADRE			NVARCHAR(10)
 ,@IDEMPRESA		NVARCHAR(10)
 ,@NIVEL			SMALLINT
 ,@EXISTE_QUERY		INT OUTPUT
) AS           
BEGIN
