		TOP 1
		@ID_DOC = ISNULL(Valor1, -1)
	FROM parametros_v2
	WHERE Descripcion = 'Pago de Poliza Muerte Natural'
	
	SELECT
		SS.PERIODO,
		SS.VALIDADO,
		SS.EMPRESA,
		SS.ID,
		SS.UNIQUE_ID,
		SS.DOC_ID,
		COALESCE(
			(
				SELECT
					TOP 1
						DD.ID_DISPUTA
				FROM DOCS_DISPUTA_CLABORAL DD
				WHERE
					DD.EMPRESA = SS.EMPRESA AND
					DD.PERIODO = SS.PERIODO AND
