	FROM DOCS_DISPUTA_CLABORAL
	WHERE
		ID_DISPUTA = @DISPUTA_ID AND
		ESTADO = 'NO'

	IF(@DISPUTA_EXISTE = 1)
		BEGIN
			INSERT INTO DOCS_DISPUTA_CLABORAL_LOG
				(
					ID_DISPUTA,
					EMPRESA,
					ID_DOC,
