					VALIDADO,
					PERIODO,
					ID_CERTIFICADO,
					USRMOD,
					FECHAMOD,
					HORAMOD,
					ACCION
				)
			VALUES 
				(
					@DDC_ID_DISPUTA,
					@DISPUTA_EMPRESA,
					@DDC_ID_DOC,
