					@USUARIO,
					@CURR_FECHA,
					@CURR_TIEMPOFULL,
					'ELIMINADO'
				)

			DELETE DOCS_DISPUTA_CLABORAL
			WHERE
				ID_DISPUTA = @DISPUTA_ID AND
				ESTADO = 'NO'
		END

		DELETE FROM ARCHIVOS
		WHERE ID = @ID

		UPDATE POLIZA_SALUD
		SET
			ID = NULL,
			VALIDADO = 'NO',
			USUARIO = @USUARIO,
			FECHAMOD = @CURR_FECHA,
			HORAMOD = @CURR_TIEMPOFULL
		WHERE
			EMPRESA = @IDEMPRESA AND
			PERIODO = @PERIODO

END
