	DECLARE cursorCampos CURSOR
		FOR
	SELECT
		DATOS.ID,
		DATOS.CAMPO,
		DATOS.NOMBRE,
		DATOS.ORDEN,
		DATOS.TIPO 
	FROM
		(SELECT ID_DOC  AS ID, 
				   ( CASE 
					   WHEN ISNULL(CAMPO, '') = '' THEN 'F' + CAST (ID_DOC AS NVARCHAR(5)) 
					   ELSE CAMPO 
