	DECLARE @LOCAL NVARCHAR(12) = ''
	DECLARE @ESTADO NVARCHAR(2) = ''
	DECLARE @DETALLE NVARCHAR(50) = ''
	DECLARE @CCHECKED BIT = 0
	DECLARE @CDISABLED BIT = 0

	IF(EXISTS(
		SELECT *
