  ,@SUMA_NOCERTIFICADOS_CTTA	INT OUTPUT
  ,@SUMA_HISTORICO_CTTA			INT OUTPUT
  ,@SUMA_FUNCIONARIOS_MDTE		INT OUTPUT
  ,@SUMA_CERTIFICADOS_MDTE		INT OUTPUT
  ,@SUMA_NOCERTIFICADOS_MDTE	INT OUTPUT
  ,@SUMA_HISTORICO_MDTE			INT OUTPUT
) AS
BEGIN

