	DECLARE @M_EMPRESA		NVARCHAR(10)
	DECLARE @M_ACRONIMO		NVARCHAR(50)
	DECLARE @M_MANDANTE		INT
	DECLARE @M_CONTEO_SI	INT
	DECLARE @M_CONTEO_NO	INT
