		D.CAMPO,
		DF.CAMPO_BD,
		DF.ID_DOC_FEC,
		D.ID_DOC,
		DFT.TIPOPASE,
		D.SUBEDOC,
		DT.TIPOPASE AS TIPOPASEDOC,
		D.ID_DOC_OPCION
	FROM DOCS_TIPOPASE AS DT
	INNER JOIN DOCS AS D
